using System;
using System.Data;
using System.Text.RegularExpressions;
using System.Windows.Forms;

public static class Snippet
{
	public static void createPreviewData(ref System.Data.DataTable dt, System.String varNames, System.String varTypes)
	{
		dt = new System.Data.DataTable();
		// Create the columns
		string[] colNames = Regex.Split(varNames, @"(?<!\\),", RegexOptions.None);
		string[] colTypes = varTypes.Split(',');
		for(int i=0; i<colNames.Length; i++)
		{
			dt.Columns.Add( colNames[i].Replace("\\,",",") );
			dt.Columns[i].DataType = System.Type.GetType("System.Decimal");
			// Set the column type if it's not numeric
			if (i<colTypes.Length)
			{
				switch (colTypes[i])
				{
					case "D":
						dt.Columns[i].DataType = System.Type.GetType("System.DateTime");
						break;
					case "A":
					case "U":
						dt.Columns[i].DataType = System.Type.GetType("System.String");
						break;
				}
			}
		}

		// Generate dummy data
		string[] lastNames = new string[] { "abbott", "acosta", "adams", "adkins", "aguilar"};
		Random rnd = new Random();
		for(int j=0; j<5; j++)
		{
			DataRow myraw = dt.NewRow();
			for(int i=0; i<colNames.Length; i++)
			{
				switch (colTypes[i])
				{
					case "D":
						DateTime start = DateTime.Now; 
						myraw[i]=start.AddDays(rnd.Next(100));
						break;
					case "A":
					case "U":
						myraw[i]= lastNames[rnd.Next(0, lastNames.Length - 1)];
						break;
					default:
						myraw[i]=rnd.Next(100);
						break;
				}
			}
			dt.Rows.Add(myraw);
		}
	}
}

using System;
using Microsoft.Office.Interop.Excel;
using System.Runtime.InteropServices;

public static class Snippet
{
	public static void refreshPreview(ref Microsoft.Office.Interop.Excel.Application excel, Microsoft.Office.Interop.Excel.XlChartType chartTypeEnum, System.Int16 chartType, System.Boolean chartByRow, System.String filename)
	{
		Microsoft.Office.Interop.Excel.Worksheet sheet = (Microsoft.Office.Interop.Excel.Worksheet)excel.ActiveSheet;
		Microsoft.Office.Interop.Excel.ChartObjects xlCharts = (Microsoft.Office.Interop.Excel.ChartObjects)sheet.ChartObjects(Type.Missing);
		Microsoft.Office.Interop.Excel.ChartObject myChart = (Microsoft.Office.Interop.Excel.ChartObject)xlCharts.Item(1);
		Microsoft.Office.Interop.Excel.Chart chartPage = myChart.Chart;

		chartPage.ChartType = (Microsoft.Office.Interop.Excel.XlChartType)System.Enum.GetValues(chartTypeEnum.GetType()).GetValue(chartType); 
		chartPage.PlotBy = (chartByRow) ? Microsoft.Office.Interop.Excel.XlRowCol.xlRows :Microsoft.Office.Interop.Excel.XlRowCol.xlColumns;
		chartPage.Export(filename,"JPEG",Type.Missing); 

		// Release Excel objects
		Marshal.ReleaseComObject(sheet);
		GC.Collect();
	}
}
